<?phpfunction furnob_add_elementor_page_settings_controls( $page ) {	$page->add_control( 'furnob_elementor_enable_sidebar_collapse',		[			'label'          => esc_html__( 'Sidebar Collapse', 'furnob-core' ),            'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'furnob' ),			'label_off'      => esc_html__( 'No', 'furnob' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);	$page->add_control( 'furnob_elementor_hide_page_header',		[			'label'          => esc_html__( 'Hide Header', 'furnob-core' ),            'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'furnob' ),			'label_off'      => esc_html__( 'No', 'furnob' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);		$page->add_control( 'furnob_elementor_page_header_type',		[			'label' => esc_html__( 'Header Type', 'furnob-core' ),			'type' => \Elementor\Controls_Manager::SELECT,			'default' => '',			'options' => [				'' => esc_html__( 'Select a type', 'furnob' ),				'type1' 	  => esc_html__( 'Type 1', 'furnob' ),				'type2'		  => esc_html__( 'Type 2', 'furnob' ),				'type3'		  => esc_html__( 'Type 3', 'furnob' ),				'type4'		  => esc_html__( 'Type 4', 'furnob' ),				'type5'		  => esc_html__( 'Type 5', 'furnob' ),			],		]	);		$page->add_control( 'furnob_elementor_page_main_header_color',		[			'label' => esc_html__( 'Main Header Background', 'furnob-core' ),			'type' => \Elementor\Controls_Manager::COLOR,			'scheme' => [				'type' => \Elementor\Core\Schemes\Color::get_type(),				'value' => \Elementor\Core\Schemes\Color::COLOR_1,			],			'default' => '#fff',			'selectors' => [				'{{WRAPPER}} .site-header .header-main' => 'background-color: {{value}};',			],		]	);		$page->add_control( 'furnob_elementor_hide_page_footer',		[			'label'          => esc_html__( 'Hide Footer', 'furnob-core' ),			'type'           => \Elementor\Controls_Manager::SWITCHER,			'label_on'       => esc_html__( 'Yes', 'furnob' ),			'label_off'      => esc_html__( 'No', 'furnob' ),			'return_value'   => 'yes',			'default'        => 'no',		]	);		$page->add_control( 'furnob_elementor_logo',		[			'label'          => esc_html__( 'Set Logo', 'furnob-core' ),            'type' 			 => \Elementor\Controls_Manager::MEDIA,		]	);		$page->add_control(		'page_width',		[			'label' => __( 'Width', 'furnob-core' ),			'type' => \Elementor\Controls_Manager::SLIDER,			'devices' => [ 'desktop' ],			'size_units' => [ 'px'],			'range' => [				'px' => [					'min' => 1100,					'max' => 1650,					'step' => 5,				],			],			'default' => [				'unit' => 'px',				'size' => 1290,			],			'selectors' => [				'{{WRAPPER}} .container' => 'max-width: {{SIZE}}{{UNIT}};',				'{{WRAPPER}} .elementor-section.elementor-section-boxed>.elementor-container' => 'max-width: {{SIZE}}{{UNIT}};',			],		]	);}add_action( 'elementor/element/wp-page/document_settings/before_section_end', 'furnob_add_elementor_page_settings_controls' );